declare module '*.scss';
declare module '*.sass';
declare module '*.css';

// Image files
declare module '*.png' {
    const src: string
    export default src
}

declare module '*.jpg' {
    const src: string
    export default src
}

declare module '*.jpeg' {
    const src: string
    export default src
}

declare module '*.gif' {
    const src: string
    export default src
}

declare module '*.webp' {
    const src: string
    export default src
}

declare module '*.ico' {
    const src: string
    export default src
}

// SVG (import as URL)
declare module '*.svg' {
    const src: string
    export default src
}
